mapper.select:
select * from (select num, title, content, category, startDate, endDate, writer, petition, petitionState, link, indicatorNum, tag, report, open, rownum r from(select * from petition
where petitionState in ${stateList}
And writer like '%' ||#{keyword}||'%' OR content like '%'||#{keyword}||'%' OR title like '%'||#{keyword}||'%'
And ${searchOption} like '%'||#{keyword}||'%'
order by num desc) order by num desc) where r >= #{startRow} and r <=#{endRow}

		select * from (select num, title, content, category, startDate, endDate, writer,
petition, petitionState, link, indicatorNum, tag, report, open,rownum r 
from(select * from petition where petitionState= #{state} order by num desc) order by num desc) 
where r >= #{startRow} and r <=#{endRow}
